import { createStore } from "redux";

import { persistStore, persistReducer } from "redux-persist";
import { rootReducer } from "./reducer/CombineREducer";
import localStorage from "redux-persist/lib/storage"; // defaults to localStorage for web and AsyncStorage for react-native

const persistConfig = {
  key: "persist-store",
  storage: localStorage,
};
const persitedReducer = persistReducer(persistConfig, rootReducer);

export const store = createStore(persitedReducer);
export const persitedStore = persistStore(store);
